    assert(VowelCount.getCount("abracadabra") == 5)
  }

  "VowelCountSpec" should "Counts vowels in empty string" in {
    assert(VowelCount.getCount("") == 0)
  }

  "VowelCountSpec" should "Counts vowels in 'pear tree'" in {
    assert(VowelCount.getCount("pear tree") == 4)
  }

  "VowelCountSpec" should "Counts vowels in 'o a kak ushakov lil vo kashu kakao'" in {
